              /* Nectar addition - tracked whether clicked or scrolled to */
                clickedNavigation = false;
              /* Nectar addition end */

                // cross-browser wheel delta
                e = e || window.event;
                var value = e.wheelDelta || -e.deltaY || -e.detail;
